import React, { useState } from "react";
import axios from "axios";
import "./index.css"; // Ensure this is included for styling
import { predictMarket } from "./api";

const App = () => {
  const [features, setFeatures] = useState(new Array(33).fill("")); // Initialize with empty strings
  const [prediction, setPrediction] = useState(null);

  // Handles input field changes
  const handleChange = (index, value) => {
    setFeatures((prevFeatures) => {
      const newFeatures = [...prevFeatures];
      newFeatures[index] = value;
      return newFeatures;
    });
  };

  // Handles form submission
  const handleSubmit = async () => {
    console.log("Submitting Features:", features); // Debugging log
    try {
      const result = await predictMarket(features.map(Number));
      setPrediction(result ? result.prediction : "Error in prediction");
    } catch (error) {
      console.error("Prediction Error:", error);
      setPrediction("Error fetching prediction");
    }
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gray-100 p-4">
      <h1 className="text-2xl font-bold mb-4">F&O Market Prediction</h1>

      {/* Two-Column Layout */}
      <div className="grid grid-cols-2 gap-4 bg-white p-6 rounded-lg shadow-lg">
        {features.map((value, index) => (
          <input
            key={index}
            type="number"
            className="border p-2 rounded w-full"
            placeholder={`Feature ${index + 1}`}
            value={value}
            onChange={(e) => handleChange(index, e.target.value)}
          />
        ))}
      </div>

      <button
        onClick={handleSubmit}
        className="mt-4 bg-blue-500 text-white px-6 py-2 rounded-lg shadow-md hover:bg-blue-600"
      >
        Predict
      </button>

      {/* Display Prediction */}
      {prediction !== null && (
        <div className="mt-4 bg-gray-200 p-4 rounded-lg shadow">
          <p className="text-lg font-semibold">Prediction: {prediction}</p>
        </div>
      )}
    </div>
  );
};

export default App;
